document.addEventListener("pointerdown",(e=>{const t=e.target.dataset;if("burger"===t.type){e.target.classList.toggle("burger--active"),document.body.classList.toggle("page--locked");document.querySelector(".header__sub-header").classList.toggle("header__sub-header--active")}if("search"===t.type){e.target.closest(".search").classList.toggle("search--shown")}if("footer-dropdown"===t.type){const t=e.target.closest(".dropdown");t.classList.add("dropdown--shown");const o=t.querySelector(".dropdown__back-layout"),s=()=>{t.classList.remove("dropdown--shown"),o.removeEventListener("pointerdown",s)};o.addEventListener("pointerdown",s)}}));